import React, { Component } from 'react';
import {connect} from 'react-redux'
import { actions } from '../redux/modules/currencies'
import _ from 'lodash'

import { currenciesSelector, isFetchingSelector } from '../redux/selectors/currenciesSelector'

class Currencies extends Component {
    componentDidMount(){
        this.props.getCurrencies()


    }
    render() {
        const {pupils, isFetching} = this.props
        return pupils && !isFetching ?
            <div>
                {
                    _.map(pupils, pupil=><div key={pupil.name}>{pupil.id} - {pupil.symbol} - {pupil.name} - {pupil.status} - {pupil.description} - {pupil.price}</div>)

                }
                <button onClick={this.handleClick.bind(this)}>Reverse</button>
            </div>
             :
            <div>Loading...</div>
    }

    handleClick(){
        this.props.reverseStudents()
    }
}

const mapDispatchToProps = {
    ...actions
}

const mapStateToProps = (state) => ({
    pupils:currenciesSelector(state),
    isFetching:isFetchingSelector(state)
})

export default connect(mapStateToProps, mapDispatchToProps)(Currencies)
