import React, { Component } from 'react';
import {connect} from 'react-redux'
import { actions } from '../redux/modules/currencies'
import _ from 'lodash'
import '../App.css';

import { currenciesSelector, isFetchingSelector } from '../redux/selectors/currenciesSelector'

class Currencies extends Component {
    componentDidMount(){
        this.props.getCurrencies()


    }
    render() {
        const {currency, isFetching} = this.props
        return currency && !isFetching ?
            <div>
                <div class="d-flex flex-row justify-content-between">
                    <div class="block">Symbool</div>
                    <div class="block">name</div>
                    <div class="block">status</div>
                    <div class="block">description</div>
                    <div class="block">price</div>
                    <div class="block">Podpiska</div>
                </div>

                <div class="col-14">
                {

                    _.map(currency, valuta=>




                            <div key={valuta.id} class="information d-flex flex-row justify-content-between">
                                <div class="block teextcurrency">{valuta.symbol}</div>
                                <div class="block">{valuta.name}</div>
                                <div class="block">{valuta.status}</div>
                                <div class="block">{valuta.description}</div>
                                <div class="block">{valuta.price}</div>

                                <button onClick=''>Підписатися</button>
                            </div>







                    )

                }
                </div>

                <button onClick={this.handleClick.bind(this)}>Reverse</button>
            </div>
             :
            <div>Loading...</div>
    }

    handleClick(){
        this.props.reverseCurrencies()
    }
}

const mapDispatchToProps = {
    ...actions
}

const mapStateToProps = (state) => ({
    currency:currenciesSelector(state),
    isFetching:isFetchingSelector(state)
})

export default connect(mapStateToProps, mapDispatchToProps)(Currencies)
